// src/screens/OrderChartScreen.js

import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useLocation } from 'react-router-dom';
import Loader from '../components/Loader';
import Message from '../components/Message';

const OrderChartScreen = () => {
    const location = useLocation();
    const data = location.state ? location.state.orders : [];

    return (
        <div style={{ padding: '25px', overflowX: 'auto' }}>
            <h1>Order Chart</h1>
            {data.length === 0 ? (
                <Message variant='info'>No orders to display</Message>
            ) : (
                <div style={{ width: '100%', height: '70vh' }}>
                    {/* Adjust height to 70% of the viewport height */}
                    <ResponsiveContainer>
                        <LineChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="createdAt" tickFormatter={(value) => value.substring(0, 10)} />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Line type="monotone" dataKey="totalPrice" stroke="#8884d8" />
                        </LineChart>
                    </ResponsiveContainer>
                </div>
            )}
        </div>
    );
};

export default OrderChartScreen;
