import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import Loader from '../components/Loader';
import Message from '../components/Message';

// Function to format date to YYYY-MM
const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    return `${year}-${month}`;
};

// Function to aggregate data by month
const aggregateDataByMonth = (orders) => {
    const monthlyData = {};

    orders.forEach(order => {
        const month = formatDate(order.createdAt);
        if (!monthlyData[month]) {
            monthlyData[month] = 0;
        }
        monthlyData[month] += order.totalPrice;
    });

    return Object.entries(monthlyData).map(([month, totalPrice]) => ({
        month,
        totalPrice
    }));
};

const OrderChartScreen = () => {
    const [monthlyData, setMonthlyData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // For demo purposes, simulate fetching from local storage
                const fetchedData = JSON.parse(localStorage.getItem('orders')) || [];
                console.log('Fetched Data:', fetchedData); // Debug log

                if (fetchedData.length === 0) {
                    setError('No orders available.');
                } else {
                    const paidOrders = fetchedData.filter(order => order.isPaid);
                    console.log('Paid Orders:', paidOrders); // Debug log
                    setMonthlyData(aggregateDataByMonth(paidOrders));
                }
            } catch (error) {
                console.error('Failed to fetch data:', error);
                setError('Failed to fetch data.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return (
            <div style={{ padding: '25px' }}>
                <Loader /> {/* Show loader while fetching data */}
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ padding: '25px' }}>
                <Message variant='danger'>{error}</Message> {/* Show error message */}
            </div>
        );
    }

    return (
        <div style={{ padding: '25px', overflowX: 'auto' }}>
            <h1>Monthly Order Chart</h1>
            {monthlyData.length === 0 ? (
                <Message variant='info'>No paid orders to display</Message>
            ) : (
                <div style={{ width: '100%', height: '70vh' }}> {/* Adjusted height to be responsive */}
                    <ResponsiveContainer>
                        <BarChart data={monthlyData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis
                                dataKey="month"
                                tickFormatter={(value) => value}
                                domain={['auto', 'auto']}
                                angle={-30} // Rotate labels for better readability
                                textAnchor="end"
                                height={80} // Add height to ensure labels are visible
                            />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar 
                                dataKey="totalPrice" 
                                fill="#007bff" // Blue color
                                barSize={20} // Slim columns
                            />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            )}
        </div>
    );
};

export default OrderChartScreen;
