import React from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import FacebookLogin from 'react-facebook-login';
import GithubLogin from 'react-oauth-github';
import TwitterLogin from 'react-twitter-login';
import axios from 'axios';

const clientIdGoogle = "681667204808-3oi01jc425li2pjov3k8vufsaiknrg1b.apps.googleusercontent.com";
const backendUrl = "http://127.0.0.1:8000";
const clientIdFacebook = "YOUR_FACEBOOK_APP_ID";
const clientIdGithub = "YOUR_GITHUB_CLIENT_ID";
const consumerKeyTwitter = "YOUR_TWITTER_CONSUMER_KEY";
const consumerSecretTwitter = "YOUR_TWITTER_CONSUMER_SECRET";

const SocialAuthButtons = () => {
  const handleGoogleSuccess = async (response) => {
    const token = response.credential;
    try {
      const res = await axios.post(
        `${backendUrl}/auth/social-auth/google/login/`,
        { token },
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Backend response:', res.data);
    } catch (error) {
      console.error('Error sending token to backend:', error);
    }
  };

  const handleGoogleError = (error) => {
    console.error('Google Login Failed:', error);
  };

  const handleFacebookResponse = async (response) => {
    const token = response.accessToken;
    try {
      const res = await axios.post(
        `${backendUrl}/auth/social-auth/facebook/login/`,
        { token },
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Backend response:', res.data);
    } catch (error) {
      console.error('Error sending token to backend:', error);
    }
  };

  const handleGithubResponse = async (response) => {
    const token = response.code;
    try {
      const res = await axios.post(
        `${backendUrl}/auth/social-auth/github/login/`,
        { token },
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Backend response:', res.data);
    } catch (error) {
      console.error('Error sending token to backend:', error);
    }
  };

  const handleTwitterResponse = async (response) => {
    const { oauth_token, oauth_token_secret } = response;
    try {
      const res = await axios.post(
        `${backendUrl}/auth/social-auth/twitter/login/`,
        { oauth_token, oauth_token_secret },
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Backend response:', res.data);
    } catch (error) {
      console.error('Error sending token to backend:', error);
    }
  };

  const buttonStyle = {
    width: '100%',
    maxWidth: '400px',
    margin: '10px',
    padding: '10px',
    borderRadius: '5px',
    fontSize: '16px',
    textAlign: 'center',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
  };

  const googleStyle = { ...buttonStyle, backgroundColor: '#4285F4' };
  const facebookStyle = { ...buttonStyle, backgroundColor: '#4267B2' };
  const githubStyle = { ...buttonStyle, backgroundColor: '#333' };
  const twitterStyle = { ...buttonStyle, backgroundColor: '#1DA1F2' };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
      <GoogleOAuthProvider clientId={clientIdGoogle}>
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onError={handleGoogleError}
          logo="google"
          style={googleStyle}
        />
      </GoogleOAuthProvider>

      <FacebookLogin
        appId={clientIdFacebook}
        autoLoad={false}
        callback={handleFacebookResponse}
        cssClass="facebook-button"
        textButton="Login with Facebook"
        style={facebookStyle}
      />

      <GithubLogin
        clientId={clientIdGithub}
        onSuccess={handleGithubResponse}
        onFailure={(error) => console.error('GitHub Login Failed:', error)}
        buttonText="Login with GitHub"
        style={githubStyle}
      />

      <TwitterLogin
        authCallback={handleTwitterResponse}
        consumerKey={consumerKeyTwitter}
        consumerSecret={consumerSecretTwitter}
        onFailure={(error) => console.error('Twitter Login Failed:', error)}
        style={twitterStyle}
      />
    </div>
  );
};

export default SocialAuthButtons;
