// src/screens/OrderChartScreen.js

import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useLocation } from 'react-router-dom';
import Loader from '../components/Loader';
import Message from '../components/Message';

const OrderChartScreen = () => {
    const location = useLocation();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulate a data fetch
        const fetchData = async () => {
            try {
                // Simulate async data fetch
                const fetchedData = location.state ? location.state.orders : [];
                setData(fetchedData);
            } catch (error) {
                console.error('Failed to fetch data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [location.state]);

    return (
        <div style={{ padding: '25px', overflowX: 'auto' }}>
            <h1>Order Chart</h1>
            {loading ? (
                <Loader /> // Show loader while fetching data
            ) : data.length === 0 ? (
                <Message variant='info'>No orders to display</Message>
            ) : (
                <div style={{ width: '100%', height: '70vh' }}> {/* Adjusted height to be responsive */}
                    <ResponsiveContainer>
                        <LineChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis
                                dataKey="createdAt"
                                tickFormatter={(value) => value.substring(0, 10)}
                                domain={['auto', 'auto']} // Adjust the domain if needed
                            />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Line type="monotone" dataKey="totalPrice" stroke="#8884d8" />
                        </LineChart>
                    </ResponsiveContainer>
                </div>
            )}
        </div>
    );
};

export default OrderChartScreen;
