import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useLocation } from 'react-router-dom';
import Loader from '../components/Loader';
import Message from '../components/Message';

// Function to format date to YYYY-MM
const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    return `${year}-${month}`;
};

// Function to aggregate data by month
const aggregateDataByMonth = (orders) => {
    const monthlyData = {};

    orders.forEach(order => {
        const month = formatDate(order.createdAt);
        if (!monthlyData[month]) {
            monthlyData[month] = 0;
        }
        monthlyData[month] += order.totalPrice;
    });

    return Object.entries(monthlyData).map(([month, totalPrice]) => ({
        month,
        totalPrice
    }));
};

// Function to aggregate data by day (optional, based on your existing code)
const aggregateDataByDay = (orders) => {
    const dailyData = {};

    orders.forEach(order => {
        const day = order.createdAt.substring(0, 10);
        if (!dailyData[day]) {
            dailyData[day] = 0;
        }
        dailyData[day] += order.totalPrice;
    });

    return Object.entries(dailyData).map(([day, totalPrice]) => ({
        day,
        totalPrice
    }));
};

const OrderChartScreen = () => {
    const location = useLocation();
    const [dailyData, setDailyData] = useState([]);
    const [monthlyData, setMonthlyData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const fetchedData = location.state ? location.state.orders : [];
                const paidOrders = fetchedData.filter(order => order.isPaid);
                setDailyData(aggregateDataByDay(paidOrders));
                setMonthlyData(aggregateDataByMonth(paidOrders));
            } catch (error) {
                console.error('Failed to fetch data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [location.state]);

    return (
        <div style={{ padding: '25px', overflowX: 'auto' }}>
            <h1>Order Charts</h1>
            {loading ? (
                <Loader /> // Show loader while fetching data
            ) : dailyData.length === 0 && monthlyData.length === 0 ? (
                <Message variant='info'>No paid orders to display</Message>
            ) : (
                <>
                    <div style={{ width: '100%', height: '50vh', marginBottom: '20px' }}> {/* Adjusted height to be responsive */}
                        <h2>Daily Order Totals</h2>
                        <ResponsiveContainer>
                            <BarChart data={dailyData}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis
                                    dataKey="day"
                                    tickFormatter={(value) => value}
                                    domain={['auto', 'auto']}
                                    angle={-30} // Rotate labels for better readability
                                    textAnchor="end"
                                />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Bar 
                                    dataKey="totalPrice" 
                                    fill="#32a852" // Green color
                                    barSize={20} // Slim columns
                                />
                            </BarChart>
                        </ResponsiveContainer>
                    </div>

                    <div style={{ width: '100%', height: '50vh' }}> {/* Adjusted height to be responsive */}
                        <h2>Monthly Order Totals</h2>
                        <ResponsiveContainer>
                            <BarChart data={monthlyData}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis
                                    dataKey="month"
                                    tickFormatter={(value) => value}
                                    domain={['auto', 'auto']}
                                    angle={-30} // Rotate labels for better readability
                                    textAnchor="end"
                                />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Bar 
                                    dataKey="totalPrice" 
                                    fill="#007bff" // Blue color
                                    barSize={20} // Slim columns
                                />
                            </BarChart>
                        </ResponsiveContainer>
                    </div>
                </>
            )}
        </div>
    );
};

export default OrderChartScreen;
