import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useLocation } from 'react-router-dom';
import Loader from '../components/Loader';
import Message from '../components/Message';

const OrderChartScreen = () => {
    const location = useLocation();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                if (location.state && location.state.orders) {
                    const fetchedData = location.state.orders;
                    const paidOrders = fetchedData.filter(order => order.isPaid);
                    setData(paidOrders);
                } else {
                    setError('No orders available.');
                }
            } catch (error) {
                console.error('Failed to fetch data:', error);
                setError('Failed to fetch data.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [location.state]);

    if (loading) {
        return (
            <div style={{ padding: '25px' }}>
                <Loader /> {/* Show loader while fetching data */}
            </div>
        );
    }

    if (error) {
        return (
            <div style={{ padding: '25px' }}>
                <Message variant='danger'>{error}</Message> {/* Show error message */}
            </div>
        );
    }

    return (
        <div style={{ padding: '25px', overflowX: 'auto' }}>
            <h1>Order Chart</h1>
            {data.length === 0 ? (
                <Message variant='info'>No paid orders to display</Message>
            ) : (
                <div style={{ width: '100%', height: '70vh' }}> {/* Adjusted height to be responsive */}
                    <ResponsiveContainer>
                        <BarChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis
                                dataKey="createdAt"
                                tickFormatter={(value) => value.substring(0, 10)}
                                domain={['auto', 'auto']}
                            />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar 
                                dataKey="totalPrice" 
                                fill="#32a852" // Green color
                                barSize={20} // Slim columns
                            />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            )}
        </div>
    );
};

export default OrderChartScreen;
