// src/screens/ProductChartScreen.js

import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useLocation } from 'react-router-dom';
import Loader from '../components/Loader';
import Message from '../components/Message';

const ProductChartScreen = () => {
    const location = useLocation();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = () => {
            try {
                // Use data passed through location state
                const fetchedData = (location.state && location.state.products) || [];

                // Aggregate data by category
                const categoryTotals = fetchedData.reduce((acc, product) => {
                    if (!acc[product.category]) {
                        acc[product.category] = { totalPrice: 0, totalStock: 0 };
                    }
                    acc[product.category].totalPrice += product.price;
                    acc[product.category].totalStock += product.countInStock; // Assuming countInStock is the stock quantity
                    return acc;
                }, {});

                // Transform aggregated data into format suitable for the chart
                const transformedData = Object.keys(categoryTotals).map(category => ({
                    name: category,
                    totalPrice: categoryTotals[category].totalPrice,
                    totalStock: categoryTotals[category].totalStock,
                }));

                setData(transformedData);
            } catch (error) {
                console.error('Failed to fetch data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [location.state]);

    return (
        <div style={{ padding: '25px', overflowX: 'auto' }}>
            <h1>Product Chart by Category</h1>
            {loading ? (
                <Loader /> // Show loader while fetching data
            ) : data.length === 0 ? (
                <Message variant='info'>No products to display</Message>
            ) : (
                <div style={{ width: '100%', height: '70vh' }}> {/* Adjusted height to be responsive */}
                    <ResponsiveContainer>
                        <BarChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis
                                dataKey="name"
                                tickFormatter={(value) => value.length > 10 ? value.substring(0, 10) + '...' : value}
                                domain={['auto', 'auto']} // Adjust the domain if needed
                            />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="totalPrice" fill="#8884d8" name="Total Price" />
                            <Bar dataKey="totalStock" fill="#82ca9d" name="Total Stock" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            )}
        </div>
    );
};

export default ProductChartScreen;
